//Import needed utilities 
import com.eviware.soapui.support.XmlHolder
import groovy.json.*

//Define a new property that will store the response from the TestStep called "Get Updated List Information"
def MsgResponseContent = (testRunner.testCase.testSteps["Add New Card"].testRequest.response.responseContent)

//Log the property, used for error checking
log.info MsgResponseContent

//Needed Property conversion
requestContent = new JsonSlurper().parseText(MsgResponseContent);

//New Property storing the ID of the Card
CardID = requestContent.id

//Log the CardID to make sure we actually are getting a CardID
log.info CardID

//Store that CardID into a project level property
testRunner.testCase.testSuite.project.setPropertyValue( "Card_ID", CardID ) 

//Store the same property at TestSuite level as well
testRunner.testCase.testSuite.setPropertyValue( "NewCard_ID", CardID )

//Get the URL of the First Card
def FirstCardURL = testRunner.testCase.testSuite.getPropertyValue( "FirstCard_URL" )

//Log the info of the FirstCard to make sure we have some content
log.info FirstCardURL

//Get the Card Comment from the TestSuite level property
def InitialCardComment = testRunner.testCase.testSuite.getPropertyValue( "InitialCard_Comment" )

//Log the info of the CardComment to make sure we have some content
log.info InitialCardComment

//Set the TestSuite property with both of the properties combined 
testRunner.testCase.testSuite.setPropertyValue( "Card_Comment", InitialCardComment+FirstCardURL) 

//Bring back the property just created
def Card_Comment = testRunner.testCase.testSuite.getPropertyValue( "Card_Comment")

//Log the info of that property to check the content
log.info Card_Comment
